function () {
  const nodes = [];
  const edges = [];
  nodes.push({
    id: FeaturesTable.selectedRow.id,
    label: FeaturesTable.selectedRow.name,
    color: "#ef4444"
  });
  FeaturesTable.selectedRow.realizedInComponentVariant.forEach(c => {
    nodes.push({
      id: c.id,
      label: c.name.en,
      color: "#064e3b"
    });
    edges.push({
      id: FeaturesTable.selectedRow.id + '/' + c.id,
      from: FeaturesTable.selectedRow.id,
      to: c.id,
      label: "realized by"
    });
    c.usesSoftwareModules.forEach(m => {
      if (!nodes.includes(n => n.id == m.id)) {
        nodes.push({
          id: m.id,
          label: m.name.en,
          color: "#b188a3"
        });
      }
      edges.push({
        id: c.id + '/' + m.id,
        from: c.id,
        to: m.id,
        label: "has software"
      });
    });
  });
  PageState.graph = {
    nodes,
    edges
  };
  return PageState.graph;
}

